mapper.select:
SELECT COUNT(*)
FROM  T1
WHERE T1.TABLE_ID = #{tableId} AND T1.TABLE_NAME = #{tableName}

mapper.insert:
INSERT INTO 
(
    REG_DT
    , REG_ID
    , TABLE_NAME
    , TABLE_ID
    , COMMENT_ID
    , CONTENTS
)
VALUES
(
    NOW()
    , #{loginUserId}
    , #{tableName}
    , #{tableId}
    , #{commentId}
    , #{contents}
)

mapper.update:
UPDATE 
SET UDT_DT = NOW()
, UDT_ID = #{loginUserId}
, CONTENTS = #{contents}
WHERE ID = #{id}
 AND REG_ID = #{loginUserId}

UPDATE 
SET DEL_DT = NOW()
, DEL_ID = #{loginUserId}
, DEL_YN = 1
WHERE ID = #{id}
 AND REG_ID = #{loginUserId}
