mapper.insert:
INSERT INTO 
(
    REG_DT
    , DEL_YN

    , ID
    , TITLE
    , OVERVIEW
    , GENRE_IDS
    , GENRE_NAMES
    , ADULT
    , ORIGINAL_LANGUAGE
    , ORIGINAL_TITLE
    , BACKDROP_PATH
    , POSTER_PATH
    , RELEASE_DATE
    , RUNTIME
    , VOTE_AVERAGE
    , VOTE_COUNT
    , POPULARITY
    , BUDGET
    , REVENUE
    , VIEW_COUNT
    , DISPLAY_YN
)
VALUES
(
    NOW()
    , 0

    , #{id}
    , #{title}
    , #{overview}
    , #{genreIds}
    , #{genreNames}
    , #{adult}
    , #{originalLanguage}
    , #{originalTitle}
    , #{backdropPath}
    , #{posterPath}
    , #{releaseDate}
    , #{runtime}
    , #{voteAverage}
    , #{voteCount}
    , #{popularity}
    , #{budget}
    , #{revenue}
    , #{viewCount}
    , #{displayYn}
)

mapper.update:
UPDATE 
SET


        VIEW_COUNT = VIEW_COUNT + 1


        UDT_DT = NOW()
        , TITLE = #{title}
        , OVERVIEW = #{overview}
        , ADULT = #{adult}
        , ORIGINAL_LANGUAGE = #{originalLanguage}
        , ORIGINAL_TITLE = #{originalTitle}
        , BACKDROP_PATH = #{backdropPath}
        , POSTER_PATH = #{posterPath}
        , RELEASE_DATE = #{releaseDate}
        , VOTE_AVERAGE = #{voteAverage}
        , VOTE_COUNT = #{voteCount}
        , POPULARITY = #{popularity}
        , DISPLAY_YN = #{displayYn}


WHERE ID = #{id}
